// src/pages/admin/Students.jsx
import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import AdminLayout from '../../layouts/AdminLayout';

const statusOptions = [
  'ALL',
  'PENDING',
  'APPROVED',
  'REJECTED',
  'DELIVERED',
  'EMITTED'
];

// Función para quitar acentos/tildes
const normalizeStr = (str = '') =>
    str
        .normalize('NFD')               // descompone caracteres acentuados
        .replace(/[\u0300-\u036f]/g, '')// elimina marcas diacríticas
        .toLowerCase();

export default function Students() {
  const [allStudents, setAllStudents] = useState([]);

  // Filtros
  const [search, setSearch] = useState('');
  const [statusFilter, setStatus] = useState('ALL');
  const [degreeFilter, setDegreeFilter] = useState('ALL');
  const [facultyFilter, setFacultyFilter] = useState('ALL');

  // Listas para selects
  const [degreesList, setDegreesList] = useState([]);
  const [facultiesList, setFacultiesList] = useState([]);

  // Helpers para display
  const buildName = s => (s.fullName || `${s.names ?? ''} ${s.surnames ?? ''}`).trim();
  const buildDeg = s =>
      s.studies?.map(st => st.degreeName).join(', ') || '—';
  const buildFac = s =>
      s.studies?.map(st => st.facultyName).join(', ') || '—';
  const buildState = s =>
      s.idCardStatus || (s.idCards?.length ? s.idCards[0].status : '—');

  // Fetch inicial
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [stuRes, degRes, facRes] = await Promise.all([
          axios.get('http://localhost:8087/uam-carnet-sys/student'),
          axios.get('http://localhost:8087/uam-carnet-sys/degree'),
          axios.get('http://localhost:8087/uam-carnet-sys/faculty'),
        ]);
        setAllStudents(Array.isArray(stuRes.data) ? stuRes.data : [stuRes.data]);
        setDegreesList(degRes.data.map(d => d.degreeName));
        setFacultiesList(facRes.data.map(f => f.facultyName));
      } catch (err) {
        console.error('Error cargando datos:', err);
      }
    };
    fetchData();
  }, []);

  // Filtrado interactivo
  const filtered = useMemo(() => {
    const term = normalizeStr(search.trim());

    return allStudents.filter(s => {
      // Normalizamos cada campo antes de comparar
      const nameNorm   = normalizeStr(buildName(s));
      const degNorm    = normalizeStr(buildDeg(s));
      const facNorm    = normalizeStr(buildFac(s));
      const cifNorm    = normalizeStr(s.cif);
      const stateNorm  = normalizeStr(buildState(s));

      // 1) Texto libre
      const textMatch =
          !term ||
          cifNorm.includes(term) ||
          nameNorm.includes(term) ||
          degNorm.includes(term) ||
          facNorm.includes(term);

      // 2) Estado
      const statusMatch =
          statusFilter === 'ALL' ||
          stateNorm === normalizeStr(statusFilter);

      // 3) Select carrera
      const degreeMatch =
          degreeFilter === 'ALL' ||
          degNorm.split(', ').some(d => d === normalizeStr(degreeFilter));

      // 4) Select facultad
      const facultyMatch =
          facultyFilter === 'ALL' ||
          facNorm.split(', ').some(f => f === normalizeStr(facultyFilter));

      return textMatch && statusMatch && degreeMatch && facultyMatch;
    });
  }, [allStudents, search, statusFilter, degreeFilter, facultyFilter]);

  return (
      <AdminLayout>
        <div className="max-w-6xl mx-auto mt-10 p-6 bg-white rounded shadow">
          <h1 className="text-2xl font-bold mb-4">Estudiantes Registrados</h1>

          {/* Barra de búsqueda / filtros */}
          <div className="flex flex-wrap items-center gap-2 mb-6">
            <input
                type="text"
                value={search}
                onChange={e => setSearch(e.target.value)}
                placeholder="Buscar por CIF, nombre, carrera o facultad…"
                className="border px-3 py-2 rounded flex-grow min-w-[200px]"
            />

            <select
                value={degreeFilter}
                onChange={e => setDegreeFilter(e.target.value)}
                className="border px-3 py-2 rounded"
            >
              <option value="ALL">Todas las carreras</option>
              {degreesList.map(deg => (
                  <option key={deg} value={deg}>{deg}</option>
              ))}
            </select>

            <select
                value={facultyFilter}
                onChange={e => setFacultyFilter(e.target.value)}
                className="border px-3 py-2 rounded"
            >
              <option value="ALL">Todas las facultades</option>
              {facultiesList.map(fac => (
                  <option key={fac} value={fac}>{fac}</option>
              ))}
            </select>

            <select
                value={statusFilter}
                onChange={e => setStatus(e.target.value)}
                className="border px-3 py-2 rounded"
            >
              {statusOptions.map(st => (
                  <option key={st} value={st}>
                    {st === 'ALL' ? 'Todos los estados' : st}
                  </option>
              ))}
            </select>
          </div>

          {/* Tabla */}
          <table className="w-full table-auto border text-left">
            <thead className="bg-gray-100">
            <tr>
              <th className="p-2">CIF</th>
              <th className="p-2">Nombre</th>
              <th className="p-2">Carrera(s)</th>
              <th className="p-2">Facultad(es)</th>
              <th className="p-2">Estado Carnet</th>
            </tr>
            </thead>
            <tbody>
            {filtered.map(s => (
                <tr key={s.cif} className="border-t">
                  <td className="p-2">{s.cif}</td>
                  <td className="p-2">{buildName(s) || '—'}</td>
                  <td className="p-2">{buildDeg(s)}</td>
                  <td className="p-2">{buildFac(s)}</td>
                  <td className="p-2">{buildState(s)}</td>
                </tr>
            ))}
            {!filtered.length && (
                <tr>
                  <td colSpan={5} className="text-center py-4 text-gray-500">
                    No se encontraron estudiantes que coincidan.
                  </td>
                </tr>
            )}
            </tbody>
          </table>
        </div>
      </AdminLayout>
  );
}
