// src/pages/superadmin/DegreeManagement.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SuperadminLayout from '../../layouts/SuperadminLayout';

export default function DegreeManagement() {
    const [degrees, setDegrees] = useState([]);
    const [faculties, setFaculties] = useState([]);
    const [editingId, setEditingId] = useState(null);
    const [nameInput, setNameInput] = useState('');
    const [facultyId, setFacultyId] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [facultyFilter, setFacultyFilter] = useState('ALL');

    const fetchAll = async () => {
        const [degRes, facRes] = await Promise.all([
            axios.get('http://localhost:8087/uam-carnet-sys/degree'),
            axios.get('http://localhost:8087/uam-carnet-sys/faculty'),
        ]);
        setDegrees(degRes.data);
        setFaculties(facRes.data);
    };

    useEffect(() => { fetchAll(); }, []);

    const startEdit = d => {
        setEditingId(d.degreeId);
        setNameInput(d.degreeName);
        setFacultyId(d.facultyId);
    };
    const cancelEdit = () => {
        setEditingId(null);
        setNameInput('');
        setFacultyId('');
    };
    const saveNew = async () => {
        if (!nameInput.trim() || !facultyId) return;
        await axios.post('http://localhost:8087/uam-carnet-sys/degree', {
            degreeName: nameInput,
            facultyId: parseInt(facultyId, 10),
        });
        setNameInput(''); setFacultyId(''); fetchAll();
    };
    const saveEdit = async id => {
        await axios.put(`http://localhost:8087/uam-carnet-sys/degree/${id}`, {
            degreeName: nameInput,
            facultyId: parseInt(facultyId, 10),
        });
        cancelEdit(); fetchAll();
    };
    const remove = async id => {
        if (window.confirm('¿Eliminar esta carrera?')) {
            await axios.delete(`http://localhost:8087/uam-carnet-sys/degree/${id}`);
            fetchAll();
        }
    };

    const filtered = degrees.filter(d => {
        const nameMatch = d.degreeName?.toLowerCase().includes(searchTerm.trim().toLowerCase());
        const facMatch =
            facultyFilter === 'ALL' ||
            (d.facultyId === parseInt(facultyFilter, 10));
        return nameMatch && facMatch;
    });

    return (
        <SuperadminLayout>
            <div className="max-w-5xl mx-auto mt-10 bg-white p-6 rounded shadow">
                <h1 className="text-2xl font-bold mb-4">Gestión de Carreras</h1>

                <div className="flex flex-wrap gap-2 mb-4">
                    <input
                        type="text"
                        placeholder="Buscar carrera…"
                        value={searchTerm}
                        onChange={e => setSearchTerm(e.target.value)}
                        className="border px-3 py-2 rounded flex-grow min-w-[200px]"
                    />
                    <select
                        value={facultyFilter}
                        onChange={e => setFacultyFilter(e.target.value)}
                        className="border px-3 py-2 rounded"
                    >
                        <option value="ALL">Todas las facultades</option>
                        {faculties.map(f => (
                            <option key={f.facultyId} value={f.facultyId}>
                                {f.facultyName}
                            </option>
                        ))}
                    </select>
                    <button
                        onClick={() => { setSearchTerm(''); setFacultyFilter('ALL'); }}
                        className="bg-gray-300 px-3 rounded"
                    >
                        Limpiar
                    </button>
                </div>

                <div className="grid grid-cols-3 gap-2 mb-6">
                    <input
                        type="text"
                        placeholder="Nueva Carrera"
                        className="border px-3 py-2 rounded col-span-2"
                        value={nameInput}
                        onChange={e => setNameInput(e.target.value)}
                    />
                    <select
                        className="border px-3 py-2 rounded"
                        value={facultyId}
                        onChange={e => setFacultyId(e.target.value)}
                    >
                        <option value="">Facultad…</option>
                        {faculties.map(f => (
                            <option key={f.facultyId} value={f.facultyId}>
                                {f.facultyName}
                            </option>
                        ))}
                    </select>
                    <div className="col-span-3 flex gap-2">
                        {editingId == null ? (
                            <button
                                onClick={saveNew}
                                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                            >
                                Añadir
                            </button>
                        ) : (
                            <>
                                <button
                                    onClick={() => saveEdit(editingId)}
                                    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                                >
                                    Guardar
                                </button>
                                <button
                                    onClick={cancelEdit}
                                    className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
                                >
                                    Cancelar
                                </button>
                            </>
                        )}
                    </div>
                </div>

                <table className="w-full table-auto border text-left">
                    <thead className="bg-gray-100">
                    <tr>
                        <th className="p-2">Carrera</th>
                        <th className="p-2">Facultad</th>
                        <th className="p-2">Acciones</th>
                    </tr>
                    </thead>
                    <tbody>
                    {filtered.map(d => (
                        <tr key={d.degreeId} className="border-t">
                            <td className="p-2">
                                {editingId === d.degreeId ? (
                                    <input
                                        value={nameInput}
                                        onChange={e => setNameInput(e.target.value)}
                                        className="border px-2 py-1 rounded w-full"
                                    />
                                ) : (
                                    d.degreeName
                                )}
                            </td>
                            <td className="p-2">
                                {editingId === d.degreeId ? (
                                    <select
                                        value={facultyId}
                                        onChange={e => setFacultyId(e.target.value)}
                                        className="border px-2 py-1 rounded w-full"
                                    >
                                        <option value="">Facultad…</option>
                                        {faculties.map(f => (
                                            <option key={f.facultyId} value={f.facultyId}>
                                                {f.facultyName}
                                            </option>
                                        ))}
                                    </select>
                                ) : (
                                    faculties.find(f => f.facultyId === d.facultyId)?.facultyName
                                )}
                            </td>
                            <td className="p-2 space-x-2">
                                {editingId !== d.degreeId && (
                                    <>
                                        <button
                                            onClick={() => startEdit(d)}
                                            className="bg-indigo-600 text-white px-3 py-1 rounded hover:bg-indigo-700"
                                        >
                                            Editar
                                        </button>
                                        <button
                                            onClick={() => remove(d.degreeId)}
                                            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                                        >
                                            Eliminar
                                        </button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                    {filtered.length === 0 && (
                        <tr>
                            <td colSpan={3} className="p-4 text-center text-gray-500">
                                No se encontraron carreras.
                            </td>
                        </tr>
                    )}
                    </tbody>
                </table>
            </div>
        </SuperadminLayout>
    );
}
