// src/pages/superadmin/FacultyManagement.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AdminLayout from '../../layouts/SuperadminLayout'; // puedes usar SuperadminLayout

export default function FacultyManagement() {
    const [faculties, setFaculties] = useState([]);
    const [editing, setEditing] = useState(null);
    const [nameInput, setNameInput] = useState('');

    const fetchAll = async () => {
        try {
            const res = await axios.get('http://localhost:8087/uam-carnet-sys/faculty');
            setFaculties(res.data);
        } catch (err) {
            console.error(err);
        }
    };

    useEffect(fetchAll, []);

    const startEdit = f => {
        setEditing(f.facultyId);
        setNameInput(f.facultyName);
    };

    const cancelEdit = () => {
        setEditing(null);
        setNameInput('');
    };

    const saveNew = async () => {
        if (!nameInput.trim()) return;
        await axios.post('http://localhost:8087/uam-carnet-sys/faculty', { facultyName: nameInput });
        setNameInput('');
        fetchAll();
    };

    const saveEdit = async id => {
        await axios.put(`http://localhost:8087/uam-carnet-sys/faculty/${id}`, { facultyName: nameInput });
        cancelEdit();
        fetchAll();
    };

    const remove = async id => {
        if (!window.confirm('¿Eliminar esta facultad?')) return;
        await axios.delete(`http://localhost:8087/uam-carnet-sys/faculty/${id}`);
        fetchAll();
    };

    return (
        <AdminLayout>
            <div className="max-w-4xl mx-auto mt-10 bg-white p-6 rounded shadow">
                <h1 className="text-2xl font-bold mb-4">Gestión de Facultades</h1>

                {/* Crear nueva */}
                <div className="flex gap-2 mb-6">
                    <input
                        type="text"
                        placeholder="Nueva Facultad"
                        className="border px-3 py-2 rounded flex-grow"
                        value={nameInput}
                        onChange={e => setNameInput(e.target.value)}
                    />
                    {editing == null ? (
                        <button
                            onClick={saveNew}
                            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                        >
                            Añadir
                        </button>
                    ) : (
                        <>
                            <button
                                onClick={() => saveEdit(editing)}
                                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                            >
                                Guardar
                            </button>
                            <button
                                onClick={cancelEdit}
                                className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
                            >
                                Cancelar
                            </button>
                        </>
                    )}
                </div>

                {/* Lista */}
                <table className="w-full table-auto border text-left">
                    <thead className="bg-gray-100">
                    <tr>
                        <th className="p-2">Facultad</th>
                        <th className="p-2">Acciones</th>
                    </tr>
                    </thead>
                    <tbody>
                    {faculties.map(f => (
                        <tr key={f.facultyId} className="border-t">
                            <td className="p-2">
                                {editing === f.facultyId ? (
                                    <input
                                        value={nameInput}
                                        onChange={e => setNameInput(e.target.value)}
                                        className="border px-2 py-1 rounded w-full"
                                    />
                                ) : (
                                    f.facultyName
                                )}
                            </td>
                            <td className="p-2 space-x-2">
                                {editing !== f.facultyId && (
                                    <>
                                        <button
                                            onClick={() => startEdit(f)}
                                            className="bg-indigo-600 text-white px-3 py-1 rounded hover:bg-indigo-700"
                                        >
                                            Editar
                                        </button>
                                        <button
                                            onClick={() => remove(f.facultyId)}
                                            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                                        >
                                            Eliminar
                                        </button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </AdminLayout>
    );
}
