// src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import AdminLayout from '../../layouts/AdminLayout';

const AdminDashboard = () => {
    const rawRole  = localStorage.getItem('role') || '';
    const cif      = localStorage.getItem('cif');
    const userRole = rawRole.toLowerCase();
    const navigate = useNavigate();

    const [admin, setAdmin] = useState(null);

    useEffect(() => {
        (async () => {
            try {
                const { data } = await axios.get(`http://localhost:8087/uam-carnet-sys/admin/byCif=${cif}`);
                const adminObj = data?.data !== undefined
                    ? Array.isArray(data.data) ? data.data[0] : data.data
                    : data;
                setAdmin(adminObj);
            } catch (err) {
                console.error('Error al cargar datos del dashboard:', err);
            }
        })();
    }, [cif]);

    const handleLogout = () => {
        localStorage.removeItem('cif');
        localStorage.removeItem('role');
        navigate('/login', { replace: true });
    };

    if (!admin) {
        return (
            <AdminLayout>
                <div className="max-w-5xl mx-auto mt-10 p-6 bg-white rounded shadow text-center">
                    Cargando…
                </div>
            </AdminLayout>
        );
    }

    const fullName = `${admin.names} ${admin.surnames}`;

    return (
        <AdminLayout>
            <div
                className="relative max-w-6xl mx-auto mt-10 p-6 bg-white bg-cover bg-center rounded shadow"
                style={{ backgroundImage: "url('/student-id-keeper.png')" }}
            >
                {/* Capa semitransparente para el fondo blanco */}
                <div className="absolute inset-0 bg-white bg-opacity-90 rounded" />

                {/* Contenido encima */}
                <div className="relative z-10">
                    <header className="flex justify-between items-center mb-6">
                        <div>
                            <h1 className="text-2xl font-bold">Panel de Control</h1>
                            <p className="text-gray-700">Bienvenido/a, {fullName}</p>
                        </div>
                        <div className="flex items-center space-x-4">
                            {userRole === 'superadmin' && (
                                <Link to="/superadmin/dashboard" className="text-indigo-600 hover:underline">
                                    ← Superadmin
                                </Link>
                            )}
                            <button onClick={handleLogout} className="text-red-600 hover:underline">
                                Cerrar sesión
                            </button>
                        </div>
                    </header>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <Link
                            to="/admin/students"
                            className="bg-blue-600 text-white p-6 rounded shadow hover:bg-blue-700 transition"
                        >
                            📚 Gestión de Estudiantes
                        </Link>
                        <Link
                            to="/admin/users"
                            className="bg-indigo-600 text-white p-6 rounded shadow hover:bg-indigo-700 transition"
                        >
                            👤 Gestión de Usuarios
                        </Link>
                    </div>
                </div>
            </div>
        </AdminLayout>
    );
};

export default AdminDashboard;
