// src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
    const rawRole = localStorage.getItem('role') || '';
    const cif     = localStorage.getItem('cif');
    const userRole = rawRole.toLowerCase();
    const navigate = useNavigate();

    const [admin, setAdmin] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const adminRes = await axios.get(`http://localhost:8087/uam-carnet-sys/admin/byCif=${cif}`);
                const payload = adminRes.data;
                const adminObj =
                    payload.data !== undefined
                        ? (Array.isArray(payload.data) ? payload.data[0] : payload.data)
                        : payload;
                setAdmin(adminObj);
            } catch (error) {
                console.error('Error al cargar datos del dashboard:', error);
            }
        };
        fetchData();
    }, [cif]);

    const handleLogout = () => {
        localStorage.removeItem('cif');
        localStorage.removeItem('role');
        navigate('/login', { replace: true });
    };

    if (!admin) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-gray-50">
                <div className="p-6 bg-white rounded shadow">Cargando...</div>
            </div>
        );
    }

    const fullName = `${admin.names} ${admin.surnames}`;

    return (
        <div className="min-h-screen bg-gray-100">
            {/* Barra Superior */}
            <header className="bg-white shadow">
                <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">
                    <h1 className="text-lg font-semibold">Panel de Control â€” Administrador</h1>
                    <div className="flex items-center space-x-4">
                        <span className="text-gray-700">Bienvenido/a, {fullName}</span>
                        {userRole === 'superadmin' && (
                            <Link
                                to="/superadmin/dashboard"
                                className="text-sm text-indigo-600 hover:underline"
                            >
                                Volver a Superadmin
                            </Link>
                        )}
                        <button
                            onClick={handleLogout}
                            className="text-sm text-red-600 hover:underline"
                        >
                            Cerrar sesiÃ³n
                        </button>
                    </div>
                </div>
            </header>

            {/* Contenido principal */}
            <main className="max-w-6xl mx-auto px-4 py-8">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <Link
                        to="/admin/students"
                        className="bg-blue-600 text-white p-6 rounded shadow hover:bg-blue-700 transition"
                    >
                        ðŸ“š GestiÃ³n de Estudiantes
                    </Link>
                    <Link
                        to="/admin/users"
                        className="bg-indigo-600 text-white p-6 rounded shadow hover:bg-indigo-700 transition"
                    >
                        ðŸ‘¤ GestiÃ³n de Usuarios
                    </Link>
                </div>
            </main>
        </div>
    );
};

export default AdminDashboard;
