// src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import AdminLayout from '../../layouts/AdminLayout';

const AdminDashboard = () => {
    const rawRole  = localStorage.getItem('role') || '';
    const cif      = localStorage.getItem('cif');
    const userRole = rawRole.toLowerCase();

    const [admin, setAdmin] = useState(null);

    useEffect(() => {
        (async () => {
            try {
                const { data } = await axios.get(`http://localhost:8087/uam-carnet-sys/admin/byCif=${cif}`);
                const adminObj = data?.data !== undefined
                    ? Array.isArray(data.data) ? data.data[0] : data.data
                    : data;
                setAdmin(adminObj);
            } catch (err) {
                console.error('Error al cargar datos del dashboard:', err);
            }
        })();
    }, [cif]);

    if (!admin) {
        return (
            <AdminLayout>
                <div className="max-w-5xl mx-auto mt-10 p-6 bg-white rounded shadow text-center">
                    Cargando‚Ä¶
                </div>
            </AdminLayout>
        );
    }

    const fullName = `${admin.names} ${admin.surnames}`;

    return (
        <AdminLayout>
            <div
                className="relative max-w-6xl mx-auto mt-10 p-6 rounded shadow"
                style={{
                    backgroundImage: "url('./images/student-id-keeper.png')",
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                }}
            >
                {/* Capa blanca semitransparente para legibilidad */}
                <div className="absolute inset-0 bg-white bg-opacity-90 rounded" />

                {/* Contenido */}
                <div className="relative z-10">
                    <h1 className="text-2xl font-bold mb-2">Bienvenido/a, {fullName}</h1>
                    <h2 className="text-xl font-semibold mb-6">Panel de Control del Administrador</h2>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                        <Link
                            to="/admin/students"
                            className="bg-blue-600 text-white p-4 rounded shadow hover:bg-blue-700 transition"
                        >
                            üìö Gesti√≥n de Estudiantes
                        </Link>
                        <Link
                            to="/admin/users"
                            className="bg-indigo-600 text-white p-4 rounded shadow hover:bg-indigo-700 transition"
                        >
                            üë§ Gesti√≥n de Usuarios
                        </Link>
                    </div>

                    {userRole === 'superadmin' && (
                        <div className="text-right">
                            <Link
                                to="/superadmin/dashboard"
                                className="inline-block bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-800 transition"
                            >
                                ‚Üê Volver a Superadmin
                            </Link>
                        </div>
                    )}
                </div>
            </div>
        </AdminLayout>
    );
};

export default AdminDashboard;
